import { createSelector } from 'reselect'


const selectShop = state => state.shop;

export const selectCollections = createSelector(
    [selectShop],
    shop => shop.collections
)

export const selectCollectionsForPreview = createSelector(
    [selectCollections],
    // we used Object.keys to change our collections object to array for working with map on collection overview
    collections => collections ? Object.keys(collections).map(key => collections[key]) : []
    // Object,keys(collections) ---> ['hat','sneakers', 'jackets' ...]
    // we map the keys to get all collections as array with  .map(key => collections[key]) -->
    // map(key => collections[hat]) , map(key => collections[sneakers]) and so on so forth
)

// to matching string from url with collection id map
export const selectCollection = collectionUrlParam =>
    createSelector(
        [selectCollections],
        collections => (collections ? collections[collectionUrlParam] : null)
    )


export const selectIsCollectionFetching = createSelector(
    [selectShop],
    shop => shop.isFetching
)

export const selectIsCollectionLoaded = createSelector(
    [selectShop],
    shop => !!shop.collections
    // we put ' !! ' before shop.collections to make it a booleane value

    // so ex. !!null === false     ,  !!0 === false  , !!NaN === false 

    // BUT any object will be true ,  !!{} === true

    // that means if  shop.collections = INITIAL_STATE = null  ===  false
    // BUT if shop.collections loaded and got the objects it will be true

    // so if there is no collections that means flase BUT if collections loaded it will be true

)

