import React from 'react';
import './shop-page.style.scss'

import CollectionsOverview from '../../collections-overview/collections-overview.component'
import CollectionPage from '../collection/collection.component'
import WithSpinner from '../../spinner/with-spinner.component'

import { connect } from 'react-redux'
import { createStructuredSelector } from 'reselect'
import { Route } from 'react-router-dom'
import { fetchingCollectionSuccessAsync } from '../../redux/shop/shop.action'
import { selectIsCollectionFetching, selectIsCollectionLoaded } from '../../redux/shop/shop.selector'


// import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase-utils'

// import { updateCollections } from '../../redux/shop/shop.action'

// add spinner when loading the items on the page
const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
    // // we need isLoading true to render the spinner when items loading
    // state = {
    //     isLoading: true
    // }

    // unsubscribeFromSnapshot = null;

    componentDidMount() {

        const { fetchingCollectionSuccessAsync } = this.props;

        fetchingCollectionSuccessAsync()

        // we used it before thunk and async

        // // to get collections items from firebase databse when page starts
        // const collectionRef = firestore.collection('collections');

        // const { updateCollections } = this.props;

        // collectionRef.onSnapshot(async snapShot => {
        //     const collectionsMap = convertCollectionsSnapshotToMap(snapShot)
        //     updateCollections(collectionsMap);
        //     this.setState({ isLoading: false })

        //     // // if we like to use get and promises

        //     // collectionRef.get().then(snapShot => {
        //     //     const collectionsMap = convertCollectionsSnapshotToMap(snapShot)
        //     //     updateCollections(collectionsMap);
        //     //     this.setState({ isLoading: false }) );
        // })
    }

    render() {
        const { match, isCollectionFetching } = this.props;
        return (
            <div className='shop-page'>
                {/* match.path refers to the same page '/shop' because it the root page here */}

                <Route exact path={`${match.path}`} render={(props) =>
                    <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} {...props} />} />

                {/* :collectionId to make it reuseable if we have more catagories or product sections */}

                <Route path={`${match.path}/:collectionId`} render={(props) =>
                    <CollectionPageWithSpinner isLoading={isCollectionFetching} {...props} />} />
            </div>
        )
    }
}

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectIsCollectionFetching
})

const mapDispatchToProps = dispatch => ({
    fetchingCollectionSuccessAsync: () => dispatch(fetchingCollectionSuccessAsync())
    // updateCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
