import { ShopActionType } from './shop.type'

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase-utils'

export const fetchingCollectionStart = () => ({
    type: ShopActionType.FETCH_COLLECTIONS_START,
})

export const fetchingCollectionSuccess = (collectionsMap) => ({
    type: ShopActionType.FETCH_COLLECTIONS_SUCCESS,
    payload: collectionsMap
})

export const fetchingCollectionFailure = errorMessage => ({
    type: ShopActionType.FETCH_COLLECTIONS_FAILURE,
    payload: errorMessage
})



export const fetchingCollectionSuccessAsync = () => {
    return dispatch => {
        const collectionRef = firestore.collection('collections');

        // to change isFetching to true
        dispatch(fetchingCollectionStart())

        collectionRef.get().then(snapShot => {
            const collectionsMap = convertCollectionsSnapshotToMap(snapShot)
            dispatch(fetchingCollectionSuccess(collectionsMap));
        }).catch(error => dispatch(fetchingCollectionFailure(error.message)))
    }
}

    // we used it before fetching and before using thunk
