import firebase from 'firebase/app'
import 'firebase/auth'
import 'firebase/firestore'

const config = {
    apiKey: "AIzaSyBJTX7Ca8i_gtEzeXX6rgaQoOfQG2tSVMY",
    authDomain: "clothy-store.firebaseapp.com",
    databaseURL: "https://clothy-store.firebaseio.com",
    projectId: "clothy-store",
    storageBucket: "clothy-store.appspot.com",
    messagingSenderId: "697380228396",
    appId: "1:697380228396:web:60dca0ec64c1be5503b089",
    measurementId: "G-12R4W3EJ8K"
}

firebase.initializeApp(config);

export const createUserProfileDocument = async (userAuth, additionalData) => {

    if (!userAuth) return;

    const userRef = firestore.doc(`users/${userAuth.uid}`)

    const snapShot = await userRef.get()

    if (!snapShot.exists) {

        const { displayName, email } = userAuth;

        const createAt = new Date();

        // create a new user from user auth to our database
        try {
            await userRef.set({
                displayName,
                email,
                createAt,
                ...additionalData
            })

        } catch (err) {
            console.log(err)
        }

    }

    return userRef;

}

// to add our shop data items to firebase database only
export const addCollectionAndDocuments = async (collectionKey, objectsToAdd) => {
    const collectionRef = firestore.collection(collectionKey);

    const batch = firestore.batch();

    objectsToAdd.forEach(obj => {
        const newDocRef = collectionRef.doc();
        console.log(newDocRef);
        batch.set(newDocRef, obj)
    });

    return await batch.commit()
}


export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
// we can use authentication on facebook , twitter or other ... twitterAuthProvider()

provider.setCustomParameters({ prompts: 'select_account' })
// to trigger the Google pop up when ever we use Google auth provider 
// for authintication or sign in with google

export const signInWithGoogle = () => auth.signInWithPopup(provider);


export default firebase;
