import React from 'react';
import './shop-page.style.scss'

import CollectionsOverview from '../../collections-overview/collections-overview.component'
import CatagoryPage from '../catagory/catagory.component'

import { Route } from 'react-router-dom'

const ShopPage = ({ match }) => (
    <div className='shop-page'>
        {/* match.path refers to the same page '/shop' because it the root page here */}
        <Route exact path={`${match.path}`} component={CollectionsOverview} />
        {/* :catagoryId to make it reuseable if we have more catagories or product sections */}
        <Route path={`${match.path}/:catagoryId`} component={CatagoryPage} />
    </div>
)


export default ShopPage
