import React, { useEffect, lazy, Suspense } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';

import Header from './component/header/header.component'
import Footer from './component/footer/footer.component'

// import HomePage from './component/pages/homepage/homepage-component'
// import ShopPage from './component/pages/shop/shop-page.component'
// import CheckoutPage from './component/pages/checkout/checkout.component'
// import ContactPage from './component/pages/contact/contact.component'

// import SignInAndSignUp from './component/pages/sign-in-and-sign-up/sign-in-and-sign-up.component'

// import { auth, createUserProfileDocument } from './component/firebase/firebase-utils'
import { addCollectionAndDocuments } from './component/firebase/firebase-utils';
import { connect } from 'react-redux'
import { createStructuredSelector } from 'reselect'

// import { setCurrentUser } from './component/redux/user/user.action'
// import { selectCollectionsForPreview } from './component/redux/shop/shop.selector'

import { selectCurrentUser } from './component/redux/user/user.selector'

import { checkUserSession } from './component/redux/user/user.action'

import './App.css';

import Spinner from './component/spinner/spinner.component';

// ErrorBoundary helps to catch the error if happend in ever component and rendering the error component 
// on the page , it 
import ErrorBoundary from './component/error-bundary/error-boundary.component'

// lazy helps to makes chunks download separetly when loading page

const HomePage = lazy(() => import('./component/pages/homepage/homepage-component'));
const ShopPage = lazy(() => import('./component/pages/shop/shop-page.component'));
const CheckoutPage = lazy(() => import('./component/pages/checkout/checkout.component'));
const ContactPage = lazy(() => import('./component/pages/contact/contact.component'));
const SignInAndSignUp = lazy(() => import('./component/pages/sign-in-and-sign-up/sign-in-and-sign-up.component'))



class App extends React.Component {
  // we don't need it bcuz we used Redux
  // constructor() {
  //   super()
  //   this.state = {
  //     currentUser: null
  //   }
  // }

  // unsubscribeFromAuth = null;

  // for authinticate Google account and keep user signed-in
  componentDidMount() {

    const { checkUserSession } = this.props;

    checkUserSession();

    // const { setCurrentUser } = this.props;

    // this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {

    //   if (userAuth) {

    //     const userRef = await createUserProfileDocument(userAuth);

    //     userRef.onSnapshot(snapShot => {
    //       setCurrentUser({
    //         id: snapShot.id,
    //         ...snapShot.data()
    //       });
    //       console.log(this.state)
    //     })
    //   }
    //   // to save user data after sign out
    //   setCurrentUser(userAuth)

    //   // // use this fn to add our data to firebase for the first time
    //   // addCollectionAndDocuments('collections', collectionsArray);
    // })
  }

  //  // to unsubscribe when user signed out
  // componentWillUnmount() {
  //   this.unsubscribeFromAuth()
  // }


  // const App = ({ checkUserSession, currentUser }) => {

  //   useEffect(() => {
  //     checkUserSession()
  //   }, [checkUserSession])

  render() {
    currentUser = this.props;
    return (
      <div>
        <Header />
        <Switch>
          <ErrorBoundary>
            {/* Suspense allows to wrap any part application that be rendering asyncronous components like lazy in Homepage */}
            <Suspense fallback={<Spinner />}>
              <Route exact path='/' component={HomePage} />
              <Route path='/shop' component={ShopPage} />
              <Route exact path='/checkout' component={CheckoutPage} />
              <Route exact path='/signin'
                render={() => currentUser ? <Redirect to='/' />
                  : <SignInAndSignUp />
                } />
              <Route exact path='/contact' component={ContactPage} />
            </Suspense>
          </ErrorBoundary>
        </Switch>
        <Footer />
      </div>
    );
  }
}


const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  // collectionsArray: selectCollectionsForPreview
})

const mapDispatchToProps = dispatch => ({
  checkUserSession: () => dispatch(checkUserSession())
})

// const mapDispatchToProps = dispatch => ({
//   setCurrentUser: user => dispatch(setCurrentUser(user))
// })

export default connect(mapStateToProps, mapDispatchToProps)(App);
