import React from 'react';

import CustomButton from '../custom-button/custom-button.component'
import FormInput from '../form-input/form-input.component'
import { auth, createUserProfileDocument } from '../firebase/firebase-utils'

import './sign-up.styles.scss'

class SignUp extends React.Component {
    constructor() {
        super()
        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();

        const { displayName, email, password, confirmPassword } = this.state;

        if (password !== confirmPassword) {
            alert("The confirm password doesn't match the password");
            return
        }

        try {
            const { user } = await auth.createUserWithEmailAndPassword(email, password)

            await createUserProfileDocument(user, { displayName });

            // this will clear our form after sign up
            this.setState({
                displayName: '',
                email: '',
                password: '',
                confirmPassword: ''
            })

        } catch (err) {
            console.log(err)
        }


    }

    handleChange = event => {

        const { name, value } = event.target;

        this.setState({ [name]: value })
    }

    render() {
        const { displayName, email, password, confirmPassword } = this.state;
        return (
            <div className="sign-up-form" onSubmit={this.handleSubmit}>
                <h1 className="title">I don't have an account !</h1>
                <p>Create new account with your email and password</p>

                <FormInput
                    name="displayName"
                    type="text"
                    value={displayName}
                    onChange={this.handleChange}
                    label="Display Name"
                    required
                />
                <FormInput
                    name="email"
                    type="text"
                    value={email}
                    onChange={this.handleChange}
                    label="Email"
                    required
                />
                <FormInput
                    name="password"
                    type="text"
                    value={password}
                    onChange={this.handleChange}
                    label="Password"
                    required
                />
                <FormInput
                    name="confirmPassword"
                    type="text"
                    value={confirmPassword}
                    onChange={this.handleChange}
                    label="Confirm Password"
                    required
                />
                <CustomButton type="submit">SIGN UP</CustomButton>
            </div>
        )
    }


}

export default SignUp;
