import { createStore, applyMiddleware } from 'redux'

import { persistStore } from 'redux-persist'

// import thunk from 'redux-thunk'

import createSagaMiddleware from 'redux-saga'

import logger from 'redux-logger'

import rootReducer from './root-reducer'

import { fetchCollectionsStart } from './shop/shop.sagas'

const sagaMiddleware = createSagaMiddleware();

const middlewares = [sagaMiddleware];

// this will show our app log in console if the app in the development mode
// that means if we deploy or open the app in the live server like heroku ('production mode') ,
// it won't be show
if (process.env.NODE_ENV === 'development') {
    middlewares.push(logger)
}

export const store = createStore(rootReducer, applyMiddleware(...middlewares))

sagaMiddleware.run(fetchCollectionsStart)

// persist used to save items and data after refreshing the page or refreshing the session
export const persistor = persistStore(store);

export default { store, persistor }
